// src/Form.js
import React, { Component } from 'react';

class Shop extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      description: '',
    };
  }

  handleInputChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    // Add your form submission logic here
    console.log('Shop data:', this.state);
  };

  handleCancel = () => {
    // Handle the cancel action (e.g., clear form fields)
    setFormData({ name: '', email: '' });
  };

  render() {
    return (
      <div>
        <h3>New Shop</h3>
        <form onSubmit={this.handleSubmit}>
          <div>
            <label>Name</label>
            <input
              type="text"
              name="name"
              value={this.state.name}
              onChange={this.handleInputChange}
            />
          </div>
          <div>
            <label>Description</label>
            <input
              type="text"
              name="description"
              value={this.state.description}
              onChange={this.handleInputChange}
            />
          </div>
          <button type="submit">Submit</button>
        </form>
      </div>
    );
  }
}

export default Shop;
