// src/Form.js
import React, { useState } from 'react';

function Shop() {
  const [formData, setFormData] = useState({
    // Define your form fields and their initial values
    name: '',
    description: ''
  });

  const handleChange = (e) => {
    // Handle input field changes and update formData
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission (e.g., save data or send a request)
    console.log('Shop form data:', formData);
  };

  const handleCancel = () => {
    // Handle the cancel action (e.g., clear form fields)
    setFormData({ name: '', email: '' });
  };

    return (
      <div>
        <h3>New Shop</h3>
        <form onSubmit={this.handleSubmit}>
          <div>
            <label>Name</label>
            <input
              type="text"
              name="name"
              value={this.state.name}
              onChange={this.handleChange}
            />
          </div>
          <div>
            <label>Description</label>
            <input
              type="text"
              name="description"
              value={this.state.description}
              onChange={this.handleChange}
            />
          </div>
          <button type="submit">Submit</button>
          <button type="button" onClick={handleCancel}>Cancel</button>
        </form>
      </div>
    );
  }


export default Shop;
